import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import { readReservation, updateReservation } from "../utils/api";
import ReservationForm from "./ReservationForm";
import ErrorAlert from "../layout/ErrorAlert";

function EditReservation() {
  const history = useHistory();
  const { reservation_id } = useParams(); // Extract reservation_id from URL
  const [error, setError] = useState(null);
  const [reservation, setReservation] = useState(null); // Initially set as null

  // Load reservation by id //
  useEffect(() => {
    const abortController = new AbortController();
    setError(null);

    async function loadReservation() {
      try {
        const response = await readReservation(reservation_id, abortController.signal);
        setReservation(response); // Ensure you're setting the reservation properly
      } catch (error) {
        setError(error);
      }
    }
    loadReservation();
    return () => abortController.abort(); // Clean up function
  }, [reservation_id]);

  // Handlers //
  const handleChange = ({ target }) => {
    setReservation({ ...reservation, [target.name]: target.value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const abortController = new AbortController();

    // Ensure people is a number
    const updatedReservation = {
      ...reservation,
      people: Number(reservation.people), // Cast people to number if necessary
    };

    updateReservation(updatedReservation, abortController.signal)
      .then(() => history.push(`/dashboard?date=${reservation.reservation_date.slice(0, 10)}`))
      .catch((error) => setError(error));

    return () => abortController.abort();
  };

  // Ensure reservation is loaded before rendering the form
  if (!reservation) return <p>Loading...</p>;

  return (
    <main>
      <div className="d-md-flex mb-3 flex-column">
        <h1>Edit Reservation</h1>
        <ErrorAlert error={error} setError={setError} />
      </div>

      <ReservationForm
        reservation={reservation}
        handleSubmit={handleSubmit}
        handleChange={handleChange}
      />
    </main>
  );
}

export default EditReservation;
