import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import { readReservation, updateReservation } from "../utils/api";
import ReservationForm from "./ReservationForm";
import ErrorAlert from "../layout/ErrorAlert";

function EditReservation() {
  const history = useHistory();
  const { reservation_id } = useParams();  // Destructure to extract reservation_id

  const [error, setError] = useState(null);
  const [reservation, setReservation] = useState({
    first_name: "",
    last_name: "",
    mobile_number: "",
    reservation_date: "",
    reservation_time: "",
    people: 1, // Default value as a number
  });

  // Load reservation by id //
  useEffect(() => {
    const abortController = new AbortController();

    async function loadReservation() {
      try {
        const response = await readReservation(Number(reservation_id), abortController.signal);  // Convert to number
        setReservation(response);
      } catch (error) {
        setError(error);
      }
    }

    loadReservation();

    return () => abortController.abort();
  }, [reservation_id]);

  // Handlers //
  const handleChange = ({ target }) => {
    setReservation({ 
      ...reservation, 
      [target.name]: target.name === "people" ? Number(target.value) : target.value  // Ensure people is a number
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const abortController = new AbortController();

    try {
      await updateReservation(Number(reservation_id), reservation, abortController.signal);  // Convert reservation_id to number
      history.push(`/dashboard?date=${reservation.reservation_date.slice(0, 10)}`);
    } catch (error) {
      setError(error);
    }

    return () => abortController.abort();
  };

  return (
    <main>
      <div className="d-md-flex mb-3 flex-column">
        <h1>Edit Reservation</h1>
        <ErrorAlert error={error} setError={setError} />
      </div>

      <ReservationForm
        reservation={reservation}
        handleSubmit={handleSubmit}
        handleChange={handleChange}
      />
    </main>
  );
}

export default EditReservation;
