import React, { useState } from "react";
import ReservationsList from "../reservations/ReservationsList";
import ErrorAlert from "../layout/ErrorAlert";
import { listReservations } from "../utils/api";
import "./Search.css";

function Search() {
  const [error, setError] = useState(null);
  const [mobile_number, setMobileNumber] = useState("");
  const [reservations, setReservations] = useState([]);
  const [reservationMessage, setReservationMessage] = useState("No reservations found");

  // Change handler for input field
  const handleChange = ({ target }) => {
    setMobileNumber(target.value);
  };

  // Handle the search function
  const handleFind = (event) => {
    event.preventDefault();

    const abortController = new AbortController();
    setError(null); // Reset error state
    setReservations([]); // Reset reservations
    setReservationMessage(""); // Reset message

    listReservations({ mobile_number }, abortController.signal)
      .then((foundReservations) => {
        if (foundReservations.length) {
          setReservations(foundReservations);
        } else {
          setReservationMessage("No reservations found");
        }
      })
      .catch((error) => setError(error));

    return () => abortController.abort();
  };

  return (
    <main className="container">
      <div className="d-md-flex mb-3 justify-content-start">
        <h1>Reservation Search</h1>
        <ErrorAlert error={error} setError={setError} />
      </div>

      {/* Search Box */}
      <div className="input-group mb-3" id="mobileSearchBox">
        <input
          type="text"
          name="mobile_number"
          className="form-control"
          onChange={handleChange}
          value={mobile_number}
          placeholder="Enter a customer's phone number"
          aria-label="Enter a customer's phone number"
          aria-describedby="button-addon2"
        />
        <button
          className="btn"
          type="submit"
          id="button-addon2"
          onClick={handleFind}
        >
          Find
        </button>
      </div>

      {/* Reservations List */}
      <div className="reservationsList">
        {reservations.length ? (
          <ReservationsList reservations={reservations} />
        ) : (
          <h3>{reservationMessage}</h3>
        )}
      </div>
    </main>
  );
}

export default Search;
