import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { createReservation } from "../utils/api";
import ReservationForm from "./ReservationForm";
import ErrorAlert from "../layout/ErrorAlert";
import "./CreateReservation.css";

function CreateReservation() {
  const history = useHistory();
  const [error, setError] = useState(null);

  const initialFormState = {
    first_name: "",
    last_name: "",
    mobile_number: "",
    reservation_date: "",
    reservation_time: "",
    people: "",
  };
  const [reservation, setReservation] = useState({ ...initialFormState });

  // Validate Form Inputs
  const validateForm = () => {
    const { first_name, last_name, mobile_number, reservation_date, reservation_time, people } = reservation;
    if (!first_name || first_name.trim() === "") {
      setError({ message: "First name is required." });
      return false;
    }
    if (!last_name || last_name.trim() === "") {
      setError({ message: "Last name is required." });
      return false;
    }
    if (!mobile_number || mobile_number.trim() === "") {
      setError({ message: "Mobile number is required." });
      return false;
    }
    if (!reservation_date || reservation_date.trim() === "") {
      setError({ message: "Reservation date is required." });
      return false;
    }
    if (!reservation_time || reservation_time.trim() === "") {
      setError({ message: "Reservation time is required." });
      return false;
    }
    if (!people || people <= 0 || isNaN(people)) {
      setError({ message: "People must be a positive number." });
      return false;
    }
    return true;
  };

  // Handle Submit
  const handleSubmit = (event) => {
    event.preventDefault();

    if (!validateForm()) return; // Exit if form validation fails

    const abortController = new AbortController();

    createReservation(reservation, abortController.signal)
      .then((newReservation) => {
        history.push(`/dashboard?date=${newReservation.reservation_date.slice(0, 10)}`);
      })
      .catch((error) => {
        if (error.name !== "AbortError") {
          setError(error); // Only handle non-abort errors
        }
      });

    return () => abortController.abort(); // Cleanup to abort fetch
  };

  return (
    <main>
      <div className="d-md-flex flex-column mb-3">
        <h1>Create a New Reservation</h1>
        <ErrorAlert error={error} setError={setError} />
      </div>

      <ReservationForm
        reservation={reservation}
        handleSubmit={handleSubmit}
        handleChange={({ target }) => setReservation({ ...reservation, [target.name]: target.value })}
      />
    </main>
  );
}

export default CreateReservation;
