import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import { readReservation, updateReservation } from "../utils/api";
import ReservationForm from "./ReservationForm";
import ErrorAlert from "../layout/ErrorAlert";

function EditReservation() {
  const history = useHistory();
  const { reservation_id } = useParams();

  const [error, setError] = useState(null);
  const [reservation, setReservation] = useState("");

  // Load reservation by id //
  useEffect(() => {
    const abortController = new AbortController();

    async function loadReservation() {
      try {
        const response = await readReservation(reservation_id, abortController.signal);
        setReservation(response);
      } catch (error) {
        if (error.name !== "AbortError") {
          setError(error);
        }
      }
    }

    loadReservation();

    return () => abortController.abort(); // Cleanup to abort fetch when unmounted
  }, [reservation_id]);

  // Validate form before submission
  const validateForm = () => {
    const { first_name, last_name, mobile_number, reservation_date, reservation_time, people } = reservation;
    if (!first_name || first_name.trim() === "") {
      setError({ message: "First name is required." });
      return false;
    }
    if (!last_name || last_name.trim() === "") {
      setError({ message: "Last name is required." });
      return false;
    }
    if (!mobile_number || mobile_number.trim() === "") {
      setError({ message: "Mobile number is required." });
      return false;
    }
    if (!reservation_date || reservation_date.trim() === "") {
      setError({ message: "Reservation date is required." });
      return false;
    }
    if (!reservation_time || reservation_time.trim() === "") {
      setError({ message: "Reservation time is required." });
      return false;
    }
    if (!people || people <= 0 || isNaN(people)) {
      setError({ message: "People must be a positive number." });
      return false;
    }
    return true;
  };

  // Handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    if (!validateForm()) return;

    const abortController = new AbortController();

    updateReservation(reservation, abortController.signal)
      .then(() => history.push(`/dashboard?date=${reservation.reservation_date.slice(0, 10)}`))
      .catch((error) => {
        if (error.name !== "AbortError") {
          setError(error); // Handle non-abort errors
        }
      });

    return () => abortController.abort(); // Cleanup
  };

  // Handle input changes
  const handleChange = ({ target }) => {
    setReservation({ ...reservation, [target.name]: target.value });
  };

  return (
    <main>
      <div className="d-md-flex mb-3 flex-column">
        <h1>Edit Reservation</h1>
        <ErrorAlert error={error} setError={setError} />
      </div>

      <ReservationForm 
        reservation={reservation}
        handleSubmit={handleSubmit}
        handleChange={handleChange}
        error={error}
      />
    </main>
  );
}

export default EditReservation;
