import React, { useState, useEffect, useMemo } from "react";
import { useParams, useHistory } from "react-router-dom";
import { listTables, readReservation, updateTable } from "../utils/api";
import ErrorAlert from "../layout/ErrorAlert";
import "./Seat.css";

function Seat() {
  const reservation_id = useParams().reservation_id;
  const history = useHistory();

  const [error, setError] = useState(null);
  const [tables, setTables] = useState([]);
  const [reservation, setReservation] = useState([]);
  const [selectedTable, setSelectedTable] = useState({
    reservation_id: reservation_id,
  });

  // Initialize AbortController to handle request cancellation //
  const abortController = useMemo(() => new AbortController(), []);

  // Load tables //
  useEffect(() => {
    async function loadTables() {
      try {
        const response = await listTables(abortController.signal);
        setTables(response);
      } catch (error) {
        setError(error);
      }
    }
    loadTables();

    // Cleanup function to abort the fetch if component unmounts
    return () => abortController.abort();
  }, [abortController]);

  // Load reservation //
  useEffect(() => {
    async function loadReservation() {
      try {
        const response = await readReservation(reservation_id, abortController.signal);
        setReservation(response);
      } catch (error) {
        setError(error);
      }
    }
    loadReservation();

    // Cleanup function to abort the fetch if component unmounts
    return () => abortController.abort();
  }, [reservation_id, abortController]);

  // Handle table selection //
  const handleChange = ({ target }) => {
    setSelectedTable({ ...selectedTable, [target.name]: target.value });
  }

  // Submit and send PUT request in order to seat reservation and update table //
  const handleSubmit = (event) => {
    event.preventDefault();
    updateTable(reservation_id, selectedTable.table_id, abortController.signal)
      .then(() => history.push("/dashboard"))
      .catch((error) => setError(error));
  }

  return (
    <main>
      <div className="d-md-flex mb-3">
        <h1>Seating</h1>
      </div>

      <ErrorAlert error={error} setError={setError} />

      <form onSubmit={handleSubmit}>
        <label htmlFor="table-select" className="table-select">
          <h4>
            Assign a table for reservation 
            #{reservation_id}: {reservation.first_name} {reservation.last_name}, 
            for {reservation.people} people:
          </h4>
        </label>
        <div className="selections">
          <select 
            name="table_id" 
            id="table-select"
            onChange={handleChange}>
          <option value="">- Please choose a table -</option>
          {tables.map((table) => (
            <option value={table.table_id} key={table.table_name}>
              {table.table_name} - {table.capacity}
            </option>
          ))}
          </select>
        </div>

        <div className="form-buttons">
          <button 
            type="submit"
            className="btn btn-primary btn-lg">
              Submit
          </button>
          <button
            type="button"
            className="btn btn-secondary btn-lg"
            onClick={() => history.go(-1)}>
              Cancel
          </button>
        </div>

      </form>
    </main>
  );
}

export default Seat;
