import React, { useEffect, useState } from "react";
import { listReservations, listTables } from "../utils/api";
import ReservationsList from "../reservations/ReservationsList";
import TablesList from "../tables/TablesList";
import DateNavButtons from "./DateNavButtons";
import ErrorAlert from "../layout/ErrorAlert";
import "./Dashboard.css";

/**
 * Defines the dashboard page.
 * @param date
 *  the date for which the user wants to view reservations.
 */
function Dashboard({ date }) {
  const [reservations, setReservations] = useState([]);
  const [reservationsError, setReservationsError] = useState(null);
  const [tables, setTables] = useState([]);
  const [tablesError, setTablesError] = useState(null);

  // Load Dashboard - reservations and tables
  useEffect(() => {
    const abortController = new AbortController();
    loadReservationsAndTables(abortController.signal);
    return () => abortController.abort();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [date]);

  async function loadReservations(signal) {
    setReservationsError(null);
    try {
      const reservationsData = await listReservations({ date }, signal);
      setReservations(reservationsData);
    } catch (error) {
      setReservationsError(error);
    }
  }

  async function loadTables(signal) {
    setTablesError(null);
    try {
      const tablesData = await listTables(signal);
      setTables(tablesData);
    } catch (error) {
      setTablesError(error);
    }
  }

  async function loadReservationsAndTables(signal) {
    await loadReservations(signal);
    await loadTables(signal);
  }

  return (
    <main className="dashboard">
      <h1>Dashboard</h1>
      <div className="d-md-flex flex-column">
        {!reservations.length && <h2>No reservations on this date.</h2>}
      </div>
      <ErrorAlert error={reservationsError} setError={setReservationsError} />

      {/* Reservations */}
      <div className="reservations-list">
        <h4 className="mb-2">Reservations for {date}</h4>
        <ReservationsList 
          reservations={reservations}
          setReservationsError={setReservationsError}
          loadReservationsAndTables={loadReservationsAndTables} 
        />
      </div>

      {/* Button Toolbar */}
      <div className="date-nav">
        <DateNavButtons currentDate={date} />
      </div>

      {/* Tables */}
      <div className="tables-list">
        <div className="d-md-flex mb-3">
          <h4 className="mb-0">Tables</h4>
        </div>
        {!tables.length && <h5 className="load-message">Loading...</h5>}
        <ErrorAlert error={tablesError} setError={setTablesError} />
        <TablesList 
          tables={tables}
          setTablesError={setTablesError}
          loadReservationsAndTables={loadReservationsAndTables} 
        />
      </div>
    </main>
  );
}

export default Dashboard;
