import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import { readReservation, updateReservation } from "../utils/api";
import ErrorAlert from "../layout/ErrorAlert";
import ReservationForm from "./ReservationForm";
import "./EditReservation.css";

function EditReservation() {
  const history = useHistory();
  const { reservation_id } = useParams();
  const [error, setError] = useState(null);

  // Set initial empty form state //
  const initialFormState = {
    first_name: "",
    last_name: "",
    mobile_number: "",
    reservation_date: "",
    reservation_time: "",
    people: "",
  };
  const [reservation, setReservation] = useState({ ...initialFormState });

  // Load existing reservation data //
  useEffect(() => {
    const abortController = new AbortController();

    readReservation(reservation_id, abortController.signal)
      .then(setReservation)
      .catch(setError);

    return () => abortController.abort();
  }, [reservation_id]);

  // Handlers //
  const handleChange = ({ target }) => {
    // Use parseInt to convert people to a number, or 0 if it can't be parsed
    const value = target.name === 'people' ? parseInt(target.value, 10) || 0 : target.value; 

    setReservation({ ...reservation, [target.name]: value });
  }

  const handleSubmit = (event) => {
    event.preventDefault();

    const abortController = new AbortController();
    updateReservation(reservation, abortController.signal)
      .then(() => history.push(`/dashboard?date=${reservation.reservation_date.slice(0, 10)}`))
      .catch(setError);

    return () => abortController.abort();
  };

  return (
    <main>
      <div className="d-md-flex flex-column mb-3">
        <h1>Edit Reservation</h1>
        <ErrorAlert error={error} setError={setError} />
      </div>

      <ReservationForm
        reservation={reservation}
        handleSubmit={handleSubmit}
        handleChange={handleChange}
      />
    </main>
  );
}

export default EditReservation;
