import React from "react";
import { useHistory } from "react-router-dom";
import { today, previous, next } from "../utils/date-time";

function DateNavButtons({ currentDate }) {
  const history = useHistory();

  // Handlers //
  const handlePrevious = (event) => {
    event.preventDefault();
    history.push(`/dashboard?date=${previous(currentDate)}`);
  }

  const handleToday = (event) => {
    event.preventDefault();
    history.push(`/dashboard?date=${today()}`);
  }

  const handleNext = (event) => {
    event.preventDefault();
    history.push(`/dashboard?date=${next(currentDate)}`);
  }

  return (
    <div className="btn-toolbar" role="toolbar" aria-label="Toolbar with button">
     <div className="btn-group" role="group">
      <button 
        type="button" 
        className="btn btn-outline-primary" /* Changed to primary color */
        style={{ color: "#ffffff", backgroundColor: "#007bff", borderColor: "#007bff" }} /* Blue background with white text */
        onClick={handlePrevious}>
          Previous
      </button>
      <button 
        type="button" 
        className="btn btn-outline-success" /* Changed to success color */
        style={{ color: "#ffffff", backgroundColor: "#28a745", borderColor: "#28a745" }} /* Green background with white text */
        onClick={handleToday}>
          Today
      </button>
      <button 
        type="button" 
        className="btn btn-outline-warning" /* Changed to warning color */
        style={{ color: "#000000", backgroundColor: "#ffc107", borderColor: "#ffc107" }} /* Yellow background with black text */
        onClick={handleNext}>
          Next
      </button>
     </div>
    </div>
  );
}

export default DateNavButtons;
